DROP TABLE IF EXISTS "user";
CREATE TABLE "user" ("uid" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "name" VARCHAR(50) NOT NULL DEFAULT "Noname", "realname" VARCHAR(100) NOT NULL, "password" VARCHAR(255) NOT NULL, "email" VARCHAR(255) NOT NULL, "currentbalance" DECIMAL(10, 5) NOT NULL DEFAULT 0, "created" DATE NOT NULL DEFAULT CURRENT_DATE, "lastactive" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);

DROP TABLE IF EXISTS "category";
CREATE TABLE "category" ("cid" INTEGER NOT NULL PRIMARY KEY, "category" VARCHAR(255) NOT NULL, "desc" VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS "message";
CREATE TABLE "message" ("mid" INTEGER NOT NULL PRIMARY KEY, "title" VARCHAR(255) NOT NULL, "body" TEXT NOT NULL, "author" INTEGER NOT NULL, "created" DATETIME NOT NULL, FOREIGN KEY ("author") REFERENCES "user" ("uid"));
DROP TABLE IF EXISTS "tipetransaksi";
CREATE TABLE "tipetransaksi" ("ttid" INTEGER NOT NULL PRIMARY KEY, "type" VARCHAR(255) NOT NULL, "desc" VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS "transaksi";
CREATE TABLE "transaksi" ("tid" INTEGER NOT NULL PRIMARY KEY, "user_id" INTEGER NOT NULL, "type_id" INTEGER NOT NULL, "info" VARCHAR(255) NOT NULL, "amount" DECIMAL(10, 5) NOT NULL, "transdate" DATETIME NOT NULL, "memo" VARCHAR(255) NOT NULL, FOREIGN KEY ("user_id") REFERENCES "user" ("uid"), FOREIGN KEY ("type_id") REFERENCES "tipetransaksi" ("ttid"));
DROP TABLE IF EXISTS "transaksidetail";
CREATE TABLE "transaksidetail" ("tdid" INTEGER NOT NULL PRIMARY KEY, "transid_id" INTEGER NOT NULL, "item" VARCHAR(255) NOT NULL, "category_id" INTEGER NOT NULL, "prices" DECIMAL(10, 5) NOT NULL, "times" DATETIME NOT NULL, "notes" VARCHAR(255) NOT NULL, FOREIGN KEY ("transid_id") REFERENCES "transaksi" ("tid"), FOREIGN KEY ("category_id") REFERENCES "category" ("cid"));

